mapper.select:
/* com.innobiz.orange.web.cu.dao.CuTaskStatBDao.selectCuTaskStatB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.STAT_NO DESC) AS RNUM,
T.STAT_NO, T.COMP_ID, T.REPRT_DT, T.SUBJ, T.NOTE, T.CONT, T.BODY_XML, T.PRIV_YN, T.CD1, T.CD2, T.CD3, T.CD4, T.CD5, T.CD6, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM,
FROM CU_TASK_STAT_BCU_WEEK_REPRT_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.cu.dao.CuTaskStatBDao.countCuTaskStatB */
SELECT COUNT(*) CNT
FROM CU_TASK_STAT_BCU_WEEK_REPRT_B T

mapper.insert:
/* com.innobiz.orange.web.cu.dao.CuTaskStatBDao.insertCuTaskStatB */ INSERT INTO CU_TASK_STAT_BCU_WEEK_REPRT_B
STAT_NO, COMP_ID, REPRT_DT, SUBJ, NOTE, CONT, BODY_XML, PRIV_YN, CD1, CD2, CD3, CD4, CD5, CD6, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{statNo, jdbcType=INTEGER}, #{compId, jdbcType=VARCHAR}, NULL,CONVERT(DATETIME, #{reprtDt, jdbcType=VARCHAR}, 120), #{subj, jdbcType=NVARCHAR}, #{note, jdbcType=NVARCHAR}, #{cont, jdbcType=NVARCHAR}, #{bodyXml, jdbcType=NVARCHAR}, #{privYn, jdbcType=CHAR}, #{cd1, jdbcType=VARCHAR}, #{cd2, jdbcType=VARCHAR}, #{cd3, jdbcType=VARCHAR}, #{cd4, jdbcType=VARCHAR}, #{cd5, jdbcType=VARCHAR}, #{cd6, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.delete:
/* com.innobiz.orange.web.cu.dao.CuTaskStatBDao.deleteCuTaskStatB */
DELETE FROM CU_TASK_STAT_BCU_WEEK_REPRT_B

	AND STAT_NO = #{statNo, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.select:
/* com.innobiz.orange.web.cu.dao.CuTaskStatFileDDao.selectCuTaskStatFileD */ SELECT
T.FILE_ID, T.REF_ID, T.DISP_NM, T.DISP_ORDR, T.FILE_EXT, T.FILE_SIZE, T.SAVE_PATH, T.USE_YN, T.REGR_UID, T.REG_DT,
FROM CU_TASK_STAT_FILE_DCU_WEEK_REPRT_FILE_D T
AND T.REF_ID = #{refId, jdbcType=VARCHAR} AND T.FILE_ID = #{fileId, jdbcType=INTEGER} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.DISP_ORDR ASC

/* com.innobiz.orange.web.cu.dao.CuTaskStatFileDDao.countCuTaskStatFileD */
SELECT COUNT(*) CNT
FROM CU_TASK_STAT_FILE_DCU_WEEK_REPRT_FILE_D T

	 AND T.REF_ID = #{refId, jdbcType=VARCHAR}
	 AND T.FILE_ID = #{fileId, jdbcType=INTEGER}
	 ${whereSqllet}

