mapper.select:
/* com.innobiz.orange.web.cu.dao.WeCdGrpBDao.selectWeCdGrpB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.CD_GRP_ID, T.COMP_ID, T.CD_GRP_NM, T.RESC_ID, (SELECT RESC_VA FROM WF_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS CD_GRP_RESC_NM, T.CD_USE_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM WE_CD_GRP_B T INNER JOIN WF_RESC_B R ON R.RESC_ID = T.RESC_ID
ORDER BY ${orderBy} ORDER BY T.REG_DT
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.cu.dao.WeCdGrpBDao.countWeCdGrpB */
SELECT COUNT(*) CNT
FROM WE_CD_GRP_B T INNER JOIN WF_RESC_B R ON R.RESC_ID = T.RESC_ID

mapper.insert:
/* com.innobiz.orange.web.cu.dao.WeCdGrpBDao.insertWeCdGrpB */ INSERT INTO WE_CD_GRP_B
CD_GRP_ID, COMP_ID, CD_GRP_NM, RESC_ID, CD_USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{cdGrpId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{cdGrpNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{cdUseYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.cu.dao.WeCdGrpBDao.updateWeCdGrpB */
UPDATE WE_CD_GRP_B

	 CD_GRP_NM = #{cdGrpNm, jdbcType=VARCHAR},
	 CD_USE_YN = #{cdUseYn, jdbcType=VARCHAR},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	 
		 MOD_DT = GETDATE(),
		 MOD_DT = NULL,
		 MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
	


	 AND CD_GRP_ID = #{cdGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.cu.dao.WeCdGrpBDao.deleteWeCdGrpB */
DELETE FROM WE_CD_GRP_B

	 AND CD_GRP_ID = #{cdGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
