mapper.select:
/* com.innobiz.orange.web.dm.dao.DmAuthDDao.selectDmAuthD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.CLS_CD DESC) AS RNUM,
	
	T.COMP_ID,
	T.CLS_CD,
	T.CD,
	T.AUTH_VA,
	T.USE_YN,

FROM DM_AUTH_D T

ORDER BY ${orderBy}
ORDER BY T.CLS_CD ASC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmAuthDDao.countDmAuthD */
SELECT COUNT(*) CNT
FROM DM_AUTH_D T

