mapper.select:
/* com.innobiz.orange.web.dm.dao.DmClsBDao.selectDmClsB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.STOR_ID, T.CLS_ID, (SELECT RESC_VA FROM DM_RESC_B WHERE STOR_ID = T.STOR_ID AND RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS CLS_NM, T.RESC_ID, T.CLS_PID, T.SORT_ORDR, T.USE_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM DM_CLS_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmClsBDao.countDmClsB */
SELECT COUNT(*) CNT
FROM DM_CLS_B T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmClsBDao.insertDmClsB */ INSERT INTO DM_CLS_B
STOR_ID, CLS_ID, CLS_NM, RESC_ID, CLS_PID, SORT_ORDR, USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{storId, jdbcType=VARCHAR}, #{clsId, jdbcType=VARCHAR}, #{clsNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{clsPid, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmClsBDao.updateDmClsB */ UPDATE DM_CLS_B
CLS_NM = #{clsNm, jdbcType=VARCHAR}, CLS_PID = #{clsPid, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, USE_YN = #{useYn, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND STOR_ID = #{storId, jdbcType=VARCHAR} AND CLS_ID = #{clsId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmClsBDao.deleteDmClsB */
DELETE FROM DM_CLS_B

	 AND STOR_ID = #{storId, jdbcType=VARCHAR}
	 AND CLS_ID = #{clsId, jdbcType=VARCHAR}
