mapper.select:
/* com.innobiz.orange.web.dm.dao.DmDocVerLDao.selectDmDocVerL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.DOC_ID DESC) AS RNUM,
	
	T.DOC_GRP_ID,
	T.DOC_ID,
	T.VER_VA,

FROM DM_${tableName}_DOC_VER_L T

ORDER BY ${orderBy}
ORDER BY T.DOC_ID ASC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmDocVerLDao.countDmDocVerL */
SELECT COUNT(*) CNT
FROM DM_${tableName}_DOC_VER_L T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmDocVerLDao.insertDmDocVerL */ INSERT INTO DM_${tableName}_DOC_VER_L
DOC_GRP_ID, DOC_ID, VER_VA,
#{docGrpId, jdbcType=VARCHAR}, #{docId, jdbcType=VARCHAR}, #{verVa, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.pt.dao.DmDocVerLDao.updateDmDocVerL */
UPDATE DM_${tableName}_DOC_VER_L

	 VER_VA = #{verVa, jdbcType=VARCHAR},


	 AND DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	 AND DOC_ID = #{docId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmDocVerLDao.deleteDmDocVerL */
DELETE FROM DM_${tableName}_DOC_VER_L

	 AND DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	 AND DOC_ID = #{docId, jdbcType=VARCHAR}
