mapper.select:
/* com.innobiz.orange.web.dm.dao.DmStorBDao.selectDmStorB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.STOR_ID, T.COMP_ID, T.TBL_NM, T.RESC_ID, T.USE_YN, T.DFT_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT, (SELECT RESC_VA FROM OR_RESC_B OR_T WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS REGR_NM , (SELECT RESC_VA FROM DM_RESC_B WHERE STOR_ID = T.STOR_ID AND RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS STOR_NM ,
FROM DM_STOR_B T , DM_RESC_B R
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmStorBDao.insertDmStorB */ INSERT INTO DM_STOR_B
STOR_ID, COMP_ID, TBL_NM, STOR_NM, RESC_ID, USE_YN, DFT_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{storId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{tblNm, jdbcType=NVARCHAR}, #{storNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{useYn, jdbcType=NVARCHAR}, #{dftYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmStorBDao.updateDmStorB */ UPDATE DM_STOR_B
TBL_NM = #{tblNm, jdbcType=VARCHAR}, STOR_NM = #{storNm, jdbcType=VARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, DFT_YN = #{dftYn, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND COMP_ID = #{compId, jdbcType=VARCHAR} AND STOR_ID = #{storId, jdbcType=VARCHAR} AND REGR_UID = #{modrUid, jdbcType=VARCHAR}

