#{emailId, jdbcType=INTEGER}, #{subj, jdbcType=VARCHAR}, #{sendNm, jdbcType=VARCHAR}, #{recvNm, jdbcType=VARCHAR}, #{cont, jdbcType=CLOB}, #{fileYn, jdbcType=VARCHAR}, GETDATE(),GETDATE(), CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

