mapper.select:
/* com.innobiz.orange.web.em.dao.CmSrchBDao.selectCmSrchB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.SRCH_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.SRCH_NO,
	T.COMP_ID,
	T.MD_RID,
	T.MD_BX_ID,
	T.MD_ID,
	T.MD_BX_RESC_ID,
	T.TBL_ID,
	T.ACT_ID,
	T.URL,
	CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT,

FROM CM_SRCH_B T

	AND T.SRCH_NO = #{srchNo, jdbcType=BIGINT}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.SRCH_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.em.dao.CmSrchBDao.countCmSrchB */
SELECT COUNT(*) CNT
FROM CM_SRCH_B T

	AND T.SRCH_NO = #{srchNo, jdbcType=BIGINT}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.em.dao.CmSrchBDao.insertCmSrchB */ INSERT INTO CM_SRCH_B
SRCH_NO, COMP_ID, MD_RID, MD_BX_ID, MD_ID, MD_BX_RESC_ID, TBL_ID, ACT_ID, URL, REG_DT,
#{srchNo, jdbcType=BIGINT}, #{compId, jdbcType=VARCHAR}, #{mdRid, jdbcType=VARCHAR}, #{mdBxId, jdbcType=VARCHAR}, #{mdId, jdbcType=VARCHAR}, #{mdBxRescId, jdbcType=VARCHAR}, #{tblId, jdbcType=VARCHAR}, #{actId, jdbcType=VARCHAR}, #{url, jdbcType=NVARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.em.dao.CmSrchBDao.updateCmSrchB */ UPDATE CM_SRCH_B
SRCH_NO = #{srchNo, jdbcType=BIGINT}, COMP_ID = #{compId, jdbcType=VARCHAR}, MD_RID = #{mdRid, jdbcType=VARCHAR}, MD_BX_ID = #{mdBxId, jdbcType=VARCHAR}, MD_ID = #{mdId, jdbcType=VARCHAR}, MD_BX_RESC_ID = #{mdBxRescId, jdbcType=VARCHAR}, TBL_ID = #{tblId, jdbcType=VARCHAR}, ACT_ID = #{actId, jdbcType=VARCHAR}, URL = #{url, jdbcType=NVARCHAR},
REG_DT = GETDATE(), REG_DT = NULL, REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
AND SRCH_NO = #{srchNo, jdbcType=BIGINT} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.em.dao.CmSrchBDao.deleteCmSrchB */
DELETE FROM CM_SRCH_B

	AND SRCH_NO = #{srchNo, jdbcType=BIGINT}
	${whereSqllet}
