mapper.select:
/* com.innobiz.orange.web.or.dao.OrUserRoleRDao.selectOrUserRoleR */

SELECT

	M.*,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'ROLE_CD' AND CD = M.ROLE_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) ROLE_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.USER_UID DESC, N.ROLE_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.USER_UID,
	T.ROLE_CD,
	
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'ROLE_CD' AND CD = T.ROLE_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) ROLE_NM,
	

FROM OR_USER_ROLE_R T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.ROLE_CD = #{roleCd, jdbcType=VARCHAR}
	AND T.USER_UID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.USER_UID, T.ROLE_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.or.dao.OrUserRoleRDao.countOrUserRoleR */
SELECT COUNT(*) CNT
FROM OR_USER_ROLE_R T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.ROLE_CD = #{roleCd, jdbcType=VARCHAR}
	AND T.USER_UID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

