mapper.select:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpAuthDDao.countPtAuthGrpAuthD */
SELECT COUNT(*) CNT
FROM PT_AUTH_GRP_AUTH_D T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR}
	AND T.EXCLI_YN = #{excliYn, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.GRP_KND_CD = #{grpKndCd, jdbcType=VARCHAR}
	AND T.GRP_ID = #{grpId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_TYP_CD = #{authGrpTypCd, jdbcType=VARCHAR}
	${whereSqllet}

