COMP_ID, AUTH_GRP_ID, EXCLI_YN, SEQ, GRP_KND_CD, GRP_ID, AUTH_GRP_TYP_CD, RESC_ID, SUB_INCL_YN, SORT_ORDR,
#{compId, jdbcType=VARCHAR}, #{authGrpId, jdbcType=VARCHAR}, #{excliYn, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, #{grpKndCd, jdbcType=VARCHAR}, #{grpId, jdbcType=VARCHAR}, #{authGrpTypCd, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{subInclYn, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM PT_AUTH_GRP_AUTH_D),

