mapper.select:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpMnuPltRDao.selectPtAuthGrpMnuPltR */

SELECT

	M.*,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'MNU_PLT_TYP_CD' AND CD = M.MNU_PLT_TYP_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) MNU_PLT_TYP_NM,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'AUTH_CD' AND CD = M.AUTH_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AUTH_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.COMP_ID DESC, N.AUTH_SCOP_CD DESC, N.AUTH_GRP_ID DESC, N.MNU_PLT_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.COMP_ID,
	T.AUTH_SCOP_CD,
	T.AUTH_GRP_ID,
	T.MNU_PLT_ID,
	T.AUTH_GRP_TYP_CD,
	T.MNU_GRP_ID,
	T.MNU_PLT_TYP_CD,
	T.AUTH_CD,
	
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'MNU_PLT_TYP_CD' AND CD = T.MNU_PLT_TYP_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) MNU_PLT_TYP_NM,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'AUTH_CD' AND CD = T.AUTH_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AUTH_NM,
	

FROM PT_AUTH_GRP_MNU_PLT_R T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.AUTH_SCOP_CD = #{authScopCd, jdbcType=VARCHAR}
	AND T.AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR}
	AND T.MNU_PLT_ID = #{mnuPltId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_TYP_CD = #{authGrpTypCd, jdbcType=VARCHAR}
	AND T.MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.COMP_ID, T.AUTH_SCOP_CD, T.AUTH_GRP_ID, T.MNU_PLT_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.pt.dao.PtAuthGrpMnuPltRDao.countPtAuthGrpMnuPltR */
SELECT COUNT(*) CNT
FROM PT_AUTH_GRP_MNU_PLT_R T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.AUTH_SCOP_CD = #{authScopCd, jdbcType=VARCHAR}
	AND T.AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR}
	AND T.MNU_PLT_ID = #{mnuPltId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_TYP_CD = #{authGrpTypCd, jdbcType=VARCHAR}
	AND T.MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpMnuPltRDao.insertPtAuthGrpMnuPltR */ INSERT INTO PT_AUTH_GRP_MNU_PLT_R
COMP_ID, AUTH_SCOP_CD, AUTH_GRP_ID, MNU_PLT_ID, AUTH_GRP_TYP_CD, MNU_GRP_ID, MNU_PLT_TYP_CD, AUTH_CD,
#{compId, jdbcType=VARCHAR}, #{authScopCd, jdbcType=VARCHAR}, #{authGrpId, jdbcType=VARCHAR}, #{mnuPltId, jdbcType=VARCHAR}, #{authGrpTypCd, jdbcType=VARCHAR}, #{mnuGrpId, jdbcType=VARCHAR}, #{mnuPltTypCd, jdbcType=VARCHAR}, #{authCd, jdbcType=VARCHAR},

