mapper.select:
/* com.innobiz.orange.web.pt.dao.PtCompLangRDao.selectPtCompLangR */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.COMP_ID DESC, N.LANG_TYP_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.COMP_ID,
	T.LANG_TYP_CD,

FROM PT_COMP_LANG_R T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.COMP_ID, T.LANG_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtCompLangRDao.countPtCompLangR */
SELECT COUNT(*) CNT
FROM PT_COMP_LANG_R T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

