mapper.select:
/* com.innobiz.orange.web.pt.dao.PtLginImgDDao.countPtLginImgD */
SELECT COUNT(*) CNT
FROM PT_LGIN_IMG_D T

	AND T.LGIN_IMG_ID = #{lginImgId, jdbcType=VARCHAR}
	AND (STRT_YMD IS NULL OR STRT_YMD <= CONVERT(DATETIME, #{strtYmd, jdbcType=VARCHAR}, 120))
	AND (END_YMD IS NULL OR END_YMD >= CONVERT(DATETIME, #{endYmd, jdbcType=VARCHAR}, 120))
	AND T.DFT_IMG_YN = #{dftImgYn, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtLginImgDDao.insertPtLginImgD */ INSERT INTO PT_LGIN_IMG_D
LGIN_IMG_ID, RESC_ID, LGIN_IMG_NM, STRT_YMD, END_YMD, DFT_IMG_YN, TOP_PX, LEFT_PX, USE_YN, LOGO_IMG_PATH, BG_IMG_PATH, MODR_UID, MOD_DT,
#{lginImgId, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{lginImgNm, jdbcType=NVARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{strtYmd, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{endYmd, jdbcType=VARCHAR}, 120), #{dftImgYn, jdbcType=VARCHAR}, NULL#{topPx, jdbcType=INTEGER}, NULL#{leftPx, jdbcType=INTEGER}, #{useYn, jdbcType=VARCHAR}, #{logoImgPath, jdbcType=VARCHAR}, #{bgImgPath, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtLginImgDDao.deletePtLginImgD */
DELETE FROM PT_LGIN_IMG_D

	AND LGIN_IMG_ID = #{lginImgId, jdbcType=VARCHAR}
	${whereSqllet}
