mapper.select:
/* com.innobiz.orange.web.pt.dao.PtMyMnuDDao.countPtMyMnuD */
SELECT COUNT(*) CNT
FROM PT_MY_MNU_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtMyMnuDDao.insertPtMyMnuD */ INSERT INTO PT_MY_MNU_D
USER_UID, MNU_LOUT_COMB_ID, MNU_LOUT_COMB_PID, COMP_ID, FLD_YN, MNU_NM, RESC_ID, MNU_ID, MNU_RESC_ID, SORT_ORDR,
#{userUid, jdbcType=VARCHAR}, #{mnuLoutCombId, jdbcType=VARCHAR}, #{mnuLoutCombPid, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{fldYn, jdbcType=VARCHAR}, #{mnuNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{mnuId, jdbcType=VARCHAR}, #{mnuRescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM PT_MY_MNU_D),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtMyMnuDDao.updatePtMyMnuD */ UPDATE PT_MY_MNU_D
USER_UID = #{userUid, jdbcType=VARCHAR}, MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR}, MNU_LOUT_COMB_PID = #{mnuLoutCombPid, jdbcType=VARCHAR}, COMP_ID = #{compId, jdbcType=VARCHAR}, FLD_YN = #{fldYn, jdbcType=VARCHAR}, MNU_NM = #{mnuNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, MNU_ID = #{mnuId, jdbcType=VARCHAR}, MNU_RESC_ID = #{mnuRescId, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
AND USER_UID = #{userUid, jdbcType=VARCHAR} AND MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtMyMnuDDao.deletePtMyMnuD */
DELETE FROM PT_MY_MNU_D

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR}
	${whereSqllet}
