mapper.select:
/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.selectPtPushAppD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.CUST_CD DESC, N.SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.CUST_CD,
	T.SEQ,
	T.CUST_NM,
	CONVERT(VARCHAR(10), T.STRT_YMD, 120) STRT_YMD,
	CONVERT(VARCHAR(10), T.END_YMD, 120) END_YMD,
	T.IPA_PATH,
	T.APK_PATH,
	T.DISP_NM,
	T.DESC_CONT,
	CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT,
	T.USE_YN,

FROM PT_PUSH_APP_D T

	AND T.CUST_CD = #{custCd, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.CUST_NM LIKE '%' + #{custNm, jdbcType=NVARCHAR} + '%'
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.CUST_CD, T.SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.countPtPushAppD */
SELECT COUNT(*) CNT
FROM PT_PUSH_APP_D T

	AND T.CUST_CD = #{custCd, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.CUST_NM LIKE '%' + #{custNm, jdbcType=NVARCHAR} + '%'
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.insertPtPushAppD */ INSERT INTO PT_PUSH_APP_D
CUST_CD, SEQ, CUST_NM, STRT_YMD, END_YMD, IPA_PATH, APK_PATH, DISP_NM, DESC_CONT, REG_DT, USE_YN,
#{custCd, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SEQ),0)+1 FROM PT_PUSH_APP_D WHERE CUST_CD = #{custCd, jdbcType=VARCHAR}), #{custNm, jdbcType=NVARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{strtYmd, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{endYmd, jdbcType=VARCHAR}, 120), #{ipaPath, jdbcType=VARCHAR}, #{apkPath, jdbcType=VARCHAR}, #{dispNm, jdbcType=NVARCHAR}, #{descCont, jdbcType=NVARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{useYn, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.deletePtPushAppD */
DELETE FROM PT_PUSH_APP_D

	AND CUST_CD = #{custCd, jdbcType=VARCHAR}
	AND SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}
