mapper.select:
/* com.innobiz.orange.web.pt.dao.PtSsoMsgTDao.selectPtSsoMsgT */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.MSG_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.MSG_ID,
	T.MSG_VA,
	CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT,

FROM PT_SSO_MSG_T T

	AND T.MSG_ID = #{msgId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.MSG_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtSsoMsgTDao.countPtSsoMsgT */
SELECT COUNT(*) CNT
FROM PT_SSO_MSG_T T

	AND T.MSG_ID = #{msgId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtSsoMsgTDao.insertPtSsoMsgT */ INSERT INTO PT_SSO_MSG_T
MSG_ID, MSG_VA, REG_DT,
#{msgId, jdbcType=VARCHAR}, #{msgVa, jdbcType=NVARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtSsoMsgTDao.updatePtSsoMsgT */
UPDATE PT_SSO_MSG_T

	MSG_ID = #{msgId, jdbcType=VARCHAR},
	MSG_VA = #{msgVa, jdbcType=NVARCHAR},
	
		REG_DT = GETDATE(),
		REG_DT = NULL,
		REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
	


	AND MSG_ID = #{msgId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtSsoMsgTDao.deletePtSsoMsgT */
DELETE FROM PT_SSO_MSG_T

	AND MSG_ID = #{msgId, jdbcType=VARCHAR}
	${whereSqllet}
