mapper.select:
/* com.innobiz.orange.web.st.dao.StSiteBDao.selectStSiteB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.SITE_ID, T.COMP_ID, T.CAT_ID, (SELECT RESC_VA FROM ST_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS SITE_NM, T.RESC_ID, T.CONT, T.SITE_URL, T.SORT_ORDR, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM ST_SITE_B T
INNER JOIN ST_RESC_B R ON T.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

mapper.insert:
/* com.innobiz.orange.web.st.dao.StSiteBDao.insertStSiteB */ INSERT INTO ST_SITE_B
SITE_ID, COMP_ID, CAT_ID, SITE_NM, RESC_ID, CONT, SITE_URL, SORT_ORDR, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{siteId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{catId, jdbcType=VARCHAR}, #{siteNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{cont, jdbcType=NVARCHAR}, #{siteUrl, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.st.dao.StSiteBDao.updateStSiteB */ UPDATE ST_SITE_B
CAT_ID = #{catId, jdbcType=VARCHAR}, SITE_NM = #{siteNm, jdbcType=NVARCHAR}, CONT = #{cont, jdbcType=NVARCHAR}, SITE_URL = #{siteUrl, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND SITE_ID = #{siteId, jdbcType=VARCHAR} AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.st.dao.StSiteBDao.deleteStSiteB */
DELETE FROM ST_SITE_B

	 AND SITE_ID = #{siteId, jdbcType=VARCHAR}
	 AND CAT_ID = #{catId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.select:
/* com.innobiz.orange.web.st.dao.StSiteBDao.maxStSiteB */
SELECT MAX(SORT_ORDR) AS SORT_ORDR
FROM ST_SITE_B

	 AND CAT_ID = #{catId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
