mapper.select:
/* com.innobiz.orange.web.wb.dao.WbBcCntcDDao.selectWbBcCntcD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.BC_ID,N.CNTC_TYP_CD DESC) AS RNUM, N.* 
	FROM (
		SELECT
	
	WBC_T.BC_ID , WBC_T.BC_CNTC_SEQ , WBC_T.CNTC_TYP_CD , WBC_T.CNTC_CONT , DFT_CNTC_YN,CNTC_CLS_CD,

FROM WB_BC_CNTC_D WBC_T


	ORDER BY ${orderBy}
	ORDER BY WBC_T.BC_ID,WBC_T.CNTC_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wb.dao.WbBcCntcDDao.countWbBcCntcD */
SELECT COUNT(*) CNT
FROM WB_BC_CNTC_D WBC_T

mapper.insert:
/* com.innobiz.orange.web.wb.dao.WbBcCntcDDao.insertWbBcCntcD */ INSERT INTO WB_BC_CNTC_D
BC_ID , BC_CNTC_SEQ , CNTC_TYP_CD , CNTC_CONT , DFT_CNTC_YN , CNTC_CLS_CD,
#{bcId, jdbcType=VARCHAR},#{bcCntcSeq, jdbcType=INTEGER},#{cntcTypCd, jdbcType=VARCHAR},#{cntcCont, jdbcType=VARCHAR},#{dftCntcYn, jdbcType=VARCHAR},#{cntcClsCd, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wb.dao.WbBcCntcDDao.updateWbBcCntcD */
UPDATE WB_BC_CNTC_D

	 CNTC_TYP_CD = #{cntcTypCd, jdbcType=VARCHAR},
	 CNTC_CONT = #{cntcCont, jdbcType=VARCHAR},
	 DFT_CNTC_YN = #{dftCntcYn, jdbcType=VARCHAR},


	 AND BC_ID = #{bcId, jdbcType=VARCHAR}
	 AND BC_CNTC_SEQ = #{bcCntcSeq, jdbcType=INTEGER}

mapper.delete:
/* com.innobiz.orange.web.wb.dao.WbBcCntcDDao.deleteWbBcCntcD */
DELETE FROM WB_BC_CNTC_D

	 AND BC_ID = #{bcId, jdbcType=VARCHAR}
	 AND BC_CNTC_SEQ = #{bcCntcSeq, jdbcType=INTEGER}

mapper.select:
/* com.innobiz.orange.web.wb.dao.WbBcCntcDDao.selectMaxCntcSeq */
SELECT MAX(BC_CNTC_SEQ) AS BC_CNTC_SEQ
FROM WB_BC_CNTC_D
WHERE BC_ID = #{bcId, jdbcType=VARCHAR}
AND CNTC_TYP_CD = #{cntcTypCd, jdbcType=VARCHAR}
