mapper.select:
/* com.innobiz.orange.web.wb.dao.WbBcMetngAtndRDao.selectWbBcMetngAtndR */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.EMPL_NM ASC) AS RNUM, N.* FROM ( SELECT
COMP_ID, REGR_UID, BC_METNG_DETL_ID, BC_METNG_ATND_DETL_ID, EMPL_TYP_CD, CASE WHEN EMPL_TYP_CD = 'EMPL' THEN (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.EMPL_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) ELSE EMPL_NM END EMPL_NM, EMPL_PHON, EMPL_ID, CASE WHEN EMPL_TYP_CD = 'EMPL' THEN (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT ORG_RESC_ID FROM OR_USER_B WHERE USER_UID = T.EMPL_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) ELSE COMP_NM END COMP_NM, COMP_PHON, EMAIL,
FROM WB_BC_METNG_ATND_R T
ORDER BY ${orderBy} ORDER BY CASE WHEN EMPL_TYP_CD = 'EMPL' THEN 1 WHEN EMPL_TYP_CD = 'FRND' THEN 2 ELSE 3 END , EMPL_NM
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

