/* com.innobiz.orange.web.wb.dao.WbBcUserLstSetupRDao.selectWbBcUserLstSetupR */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.ATRB_ID ASC) AS RNUM, N.* 
	FROM (
		SELECT
	
	WBULS_T.COMP_ID , WBULS_T.REGR_UID , WBULS_T.ATRB_ID , WBULS_T.SORT_ORDR , WBULS_T.MSG_ID , WBULS_T.ALN_VA , WBULS_T.WDTH_PERC , WBULS_T.DISP_YN , 

FROM WB_BC_USER_LST_SETUP_R WBULS_T


	ORDER BY ${orderBy}
	ORDER BY WBULS_T.SORT_ORDR ASC


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wb.dao.WbBcUserLstSetupRDao.countWbBcUserLstSetupR */
SELECT COUNT(*) CNT
FROM WB_BC_USER_LST_SETUP_R WBULS_T

