mapper.select:
/* com.innobiz.orange.web.wc.dao.WcSchdlGrpBDao.selectWcSchdlGrpB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.SCHDL_GRP_ID DESC) AS RNUM, N.* FROM ( SELECT
WSG_T.SCHDL_GRP_ID, WSG_T.COMP_ID, WSG_T.GRP_NM, WSG_T.REGR_UID, WSG_T.REGR_NM, WSG_T.MASTR_UID, WSG_T.REG_DT
,T.USER_UID, T.USER_NM, T.MBSH_TYP_CD, T.AUTH_GRAD_CD, T.EMAIL_SEND_YN
FROM WC_SCHDL_GRP_B WSG_T
ORDER BY ${orderBy} ORDER BY WSG_T.COMP_ID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcSchdlGrpBDao.countWcSchdlGrpB */
SELECT COUNT(*) CNT
FROM WC_SCHDL_GRP_B WSG_T

mapper.insert:
/* com.innobiz.orange.web.wc.dao.WcSchdlGrpBDao.insertWcSchdlGrpB */ INSERT INTO WC_SCHDL_GRP_B
SCHDL_GRP_ID, COMP_ID, GRP_NM, REGR_UID, REGR_NM, MASTR_UID, REG_DT,
#{schdlGrpId, jdbcType=VARCHAR}, #{compId, jdbcType=CHAR}, #{grpNm, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR}, #{regrNm, jdbcType=VARCHAR}, #{mastrUid, jdbcType=VARCHAR}, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wc.dao.WcSchdlGrpBDao.updateWcSchdlGrpB */
UPDATE WC_SCHDL_GRP_B

   
	 GRP_NM = #{grpNm, jdbcType=VARCHAR},
	 REGR_UID = #{regrUid, jdbcType=VARCHAR},
	 REGR_NM = #{regrNm, jdbcType=VARCHAR},
	 MASTR_UID = #{mastrUid, jdbcType=VARCHAR},
	 REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
	
	


	AND SCHDL_GRP_ID = #{schdlGrpId, jdbcType=VARCHAR}
	AND COMP_ID = #{compId, jdbcType=CHAR}
