mapper.select:
/* com.innobiz.orange.web.wc.dao.WcWorkSchdlBDao.selectWcWorkSchdlB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.SCHDL_ID DESC) AS RNUM, N.* FROM (SELECT
T.SCHDL_ID, T.COMP_ID, T.USER_UID, T.SCHDL_TYP_CD, T.SUBJ, T.CONT, T.STRT_DT, T.END_DT, T.ALLDAY_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT, (SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'WORK_SCHDL_CD' AND CD = T.SCHDL_TYP_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS SCHDL_TYP_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) REGR_NM,
FROM WC_WORK_SCHDL_B T
ORDER BY ${orderBy} ORDER BY T.SCHDL_ID DESC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcWorkSchdlBDao.countWcWorkSchdlB */
SELECT COUNT(*) CNT
FROM WC_WORK_SCHDL_B T

