#{schdlId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, #{schdlTypCd, jdbcType=VARCHAR}, #{subj, jdbcType=NVARCHAR}, #{cont, jdbcType=NVARCHAR}, NULL,CONVERT(DATETIME, #{strtDt, jdbcType=VARCHAR}, 120), NULL,CONVERT(DATETIME, #{endDt, jdbcType=VARCHAR}, 120), #{alldayYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

