mapper.select:
/* com.innobiz.orange.web.wf.dao.WfFormGenProcLDao.selectWfFormGenProcL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.GEN_ID DESC, N.SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.GEN_ID,
	T.FORM_NO,
	T.SEQ,
	T.PROC_CONT,
	T.ALL_CNT,
	T.PROC_CNT,
	T.STRT_DT,
	T.END_DT,
	T.ERR_CONT,
	T.ERR_YN,

FROM WF_FORM_GEN_PROC_L T

	AND T.GEN_ID = #{genId, jdbcType=VARCHAR}
	AND T.FORM_NO = #{formNo, jdbcType=INTEGER}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.SEQ >= #{strtSeq, jdbcType=NUMERIC}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.GEN_ID, T.SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wf.dao.WfFormGenProcLDao.countWfFormGenProcL */
SELECT COUNT(*) CNT
FROM WF_FORM_GEN_PROC_L T

	AND T.GEN_ID = #{genId, jdbcType=VARCHAR}
	AND T.FORM_NO = #{seq, jdbcType=INTEGER}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.SEQ >= #{strtSeq, jdbcType=NUMERIC}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wf.dao.WfFormGenProcLDao.insertWfFormGenProcL */ INSERT INTO WF_FORM_GEN_PROC_L
GEN_ID, FORM_NO, SEQ, PROC_CONT, ALL_CNT, PROC_CNT, STRT_DT, END_DT, ERR_CONT, ERR_YN,
#{genId, jdbcType=VARCHAR}, #{formNo, jdbcType=INTEGER}, #{seq, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SEQ),0)+1 FROM WF_FORM_GEN_PROC_L WHERE GEN_ID = #{genId, jdbcType=VARCHAR}), #{procCont, jdbcType=VARCHAR}, NULL#{allCnt, jdbcType=BIGINT}, NULL#{procCnt, jdbcType=BIGINT}, GETDATE(),NULL, CONVERT(DATETIME, #{strtDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{endDt, jdbcType=VARCHAR}, 120), #{errCont, jdbcType=VARCHAR}, #{errYn, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wf.dao.WfFormGenProcLDao.updateWfFormGenProcL */ UPDATE WF_FORM_GEN_PROC_L
PROC_CONT = #{procCont, jdbcType=VARCHAR}, ALL_CNT = NULL#{allCnt, jdbcType=BIGINT}, PROC_CNT = NULL#{procCnt, jdbcType=BIGINT},
STRT_DT = GETDATE(), STRT_DT = NULL, STRT_DT = CONVERT(DATETIME, #{strtDt, jdbcType=VARCHAR}, 120),
END_DT = GETDATE(), END_DT = NULL, END_DT = CONVERT(DATETIME, #{endDt, jdbcType=VARCHAR}, 120),
ERR_CONT = #{errCont, jdbcType=VARCHAR}, ERR_YN = #{errYn, jdbcType=VARCHAR},
AND GEN_ID = #{genId, jdbcType=VARCHAR} AND FORM_NO = #{formNo, jdbcType=INTEGER} AND SEQ = #{seq, jdbcType=INTEGER} ${whereSqllet}

