SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.GEN_ID DESC, N.TRAN_DATA_TYP_CD DESC, N.SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.GEN_ID,
	T.FORM_NO,
	T.SEQ,
	T.WORK_NO,

FROM WF_FORM_GEN_TGT_T T

	AND T.GEN_ID = #{genId, jdbcType=VARCHAR}
	AND T.FORM_NO = #{formNo, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=BIGINT}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.GEN_ID, T.TRAN_DATA_TYP_CD, T.SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wf.dao.WfFormGenTgtTDao.countWfFormGenTgtT */
SELECT COUNT(*) CNT
FROM WF_FORM_GEN_TGT_T T

	AND T.GEN_ID = #{genId, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=BIGINT}
	${whereSqllet}

