mapper.select:
	/* com.innobiz.orange.web.wh.dao.WhPichGrpLDao.selectWhPichGrpL */
	
	SELECT * FROM (
	
	SELECT
	
		
		TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.SORT_ORDR ASC) AS RNUM, N.* FROM (SELECT
		
		T.PICH_GRP_ID,
        T.USER_UID,
        (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
        T.SORT_ORDR,
	
	FROM WH_PICH_GRP_L T
	
	
		ORDER BY ${orderBy}
		ORDER BY T.SORT_ORDR ASC
	
	
	) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhPichGrpLDao.countWhPichGrpL */
SELECT COUNT(*) CNT
FROM WH_PICH_GRP_L T

mapper.insert:
/* com.innobiz.orange.web.wh.dao.WhPichGrpLDao.insertWhPichGrpL */ INSERT INTO WH_PICH_GRP_L
PICH_GRP_ID, USER_UID, SORT_ORDR,
#{pichGrpId, jdbcType=INTEGER}, #{userUid, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER},

mapper.update:
/* com.innobiz.orange.web.wh.dao.WhPichGrpLDao.updateWhPichGrpL */
UPDATE WH_PICH_GRP_L

	 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	 AND PICH_GRP_ID = #{pichGrpId, jdbcType=VARCHAR}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wh.dao.WhPichGrpLDao.deleteWhPichGrpL */
DELETE FROM WH_PICH_GRP_L

	 AND PICH_GRP_ID = #{pichGrpId, jdbcType=VARCHAR}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
