mapper.select:
	/* com.innobiz.orange.web.wh.dao.WhResEvalBDao.selectWhResEvalB */
	
	SELECT * FROM (
	
	SELECT
	
		
		TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.SORT_ORDR ASC) AS RNUM, N.* FROM (SELECT
		
		T.EVAL_NO,
        T.COMP_ID,
        (SELECT RESC_VA FROM WH_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS EVAL_NM,
        T.RESC_ID,
        T.SORT_ORDR,
	
	FROM WH_RES_EVAL_B T
	
	
		ORDER BY ${orderBy}
		ORDER BY T.SORT_ORDR ASC
	
	
	) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhResEvalBDao.countWhResEvalB */
SELECT COUNT(*) CNT
FROM WH_RES_EVAL_B T

mapper.insert:
/* com.innobiz.orange.web.wh.dao.WhResEvalBDao.insertWhResEvalB */ INSERT INTO WH_RES_EVAL_B
EVAL_NO, COMP_ID, EVAL_NM, RESC_ID, SORT_ORDR,
#{evalNo, jdbcType=INTEGER}, #{compId, jdbcType=VARCHAR}, #{evalNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER},

mapper.update:
/* com.innobiz.orange.web.wh.dao.WhResEvalBDao.updateWhResEvalB */
UPDATE WH_RES_EVAL_B

	 EVAL_NM = #{evalNm, jdbcType=VARCHAR},
	 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},			


	 AND EVAL_NO = #{evalNo, jdbcType=INTEGER}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wh.dao.WhResEvalBDao.deleteWhResEvalB */
DELETE FROM WH_RES_EVAL_B

	 AND EVAL_NO = #{evalNo, jdbcType=INTEGER}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
