mapper.select:
/* com.innobiz.orange.web.cu.dao.WjWorkReprtBDao.selectWjWorkReprtB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.REPRT_NO, T.COMP_ID, T.ORG_ID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_ORG_B WHERE ORG_ID = T.ORG_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS ORG_RESC_NM, T.SUBJ, T.CONT, T.REGR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM WJ_WORK_REPRT_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.cu.dao.WjWorkReprtBDao.countWjWorkReprtB */
SELECT COUNT(*) CNT
FROM WJ_WORK_REPRT_B T

