/* com.innobiz.orange.web.wl.dao.WlReprtGrpBDao.insertWlReprtGrpB */ INSERT INTO WL_REPRT_GRP_B
GRP_NO, COMP_ID, GRP_NM, RESC_ID, USE_YN, DFT_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{grpNo, jdbcType=INTEGER}, #{compId, jdbcType=VARCHAR}, #{grpNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{useYn, jdbcType=CHAR}, #{dftYn, jdbcType=CHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.pt.dao.WlReprtGrpBDao.updateWlReprtGrpB */ UPDATE WL_REPRT_GRP_B
GRP_NM = #{grpNm, jdbcType=VARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, DFT_YN = #{dftYn, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND GRP_NO = #{grpNo, jdbcType=VARCHAR} ${whereSqllet}

