mapper.select:
/* com.innobiz.orange.web.wl.dao.WlReprtGrpLDao.selectWlReprtGrpL */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.SORT_ORDR ASC) AS RNUM,
T.GRP_NO, T.USER_UID, T.TGT_TYP_CD, T.REPRT_TYP_CD, T.SORT_ORDR, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
FROM WL_REPRT_GRP_L T
ORDER BY ${orderBy} ORDER BY T.SORT_ORDR ASC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wl.dao.WlReprtGrpLDao.countWlReprtGrpL */
SELECT COUNT(*) CNT
FROM WL_REPRT_GRP_L T

