mapper.select:
/* com.innobiz.orange.web.wl.dao.WlTaskLogBDao.selectWlTaskLogB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.REG_DT DESC) AS RNUM,
T.LOG_NO, T.COMP_ID, T.DEPT_ID, T.TYP_CD, T.REPRT_DT, T.GRP_NO, T.SUBJ,
T.RESULT_CONT, T.PLAN_CONT, T.ETC_CONT,
T.STAT_CD, T.CONSOL_YN, T.REGR_UID, T.REG_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, T.MODR_UID, T.MOD_DT,
FROM WL_TASK_LOG_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wl.dao.WlTaskLogBDao.countWlTaskLogB */
SELECT COUNT(*) CNT
FROM WL_TASK_LOG_B T

