#{logNo, jdbcType=INTEGER}, #{compId, jdbcType=VARCHAR}, #{deptId, jdbcType=VARCHAR}, #{typCd, jdbcType=VARCHAR}, CONVERT(DATETIME, #{reprtDt, jdbcType=VARCHAR}, 120), #{grpNo, jdbcType=INTEGER}, #{subj, jdbcType=VARCHAR}, #{resultCont, jdbcType=VARCHAR}, #{planCont, jdbcType=VARCHAR}, #{etcCont, jdbcType=VARCHAR}, #{statCd, jdbcType=VARCHAR}, #{consolYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

