mapper.select:
/* com.innobiz.orange.web.wo.dao.WoOnecAttFileDDao.selectWoOnecAttFileD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.ONEC_NO DESC, N.ATT_SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.ONEC_NO,
	T.ATT_SEQ,
	T.DISP_NM,
	T.FILE_EXT,
	T.FILE_SIZE,
	T.SAVE_PATH,
	T.REGR_UID,
	CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT,

FROM WO_ONEC_ATT_FILE_D T

	AND T.ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND T.ATT_SEQ = #{attSeq, jdbcType=INTEGER}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.ONEC_NO, T.ATT_SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wo.dao.WoOnecAttFileDDao.countWoOnecAttFileD */
SELECT COUNT(*) CNT
FROM WO_ONEC_ATT_FILE_D T

	AND T.ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND T.ATT_SEQ = #{attSeq, jdbcType=INTEGER}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wo.dao.WoOnecAttFileDDao.insertWoOnecAttFileD */ INSERT INTO WO_ONEC_ATT_FILE_D
ONEC_NO, ATT_SEQ, DISP_NM, FILE_EXT, FILE_SIZE, SAVE_PATH, REGR_UID, REG_DT,
#{onecNo, jdbcType=BIGINT}, #{attSeq, jdbcType=INTEGER}, (SELECT ISNULL(MAX(ATT_SEQ),0)+1 FROM WO_ONEC_ATT_FILE_D WHERE ONEC_NO = #{onecNo, jdbcType=VARCHAR}), #{dispNm, jdbcType=NVARCHAR}, #{fileExt, jdbcType=VARCHAR}, NULL#{fileSize, jdbcType=INTEGER}, #{savePath, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wo.dao.WoOnecAttFileDDao.updateWoOnecAttFileD */ UPDATE WO_ONEC_ATT_FILE_D
ONEC_NO = #{onecNo, jdbcType=BIGINT}, ATT_SEQ = #{attSeq, jdbcType=INTEGER}, DISP_NM = #{dispNm, jdbcType=NVARCHAR}, FILE_EXT = #{fileExt, jdbcType=VARCHAR}, FILE_SIZE = NULL#{fileSize, jdbcType=INTEGER}, SAVE_PATH = #{savePath, jdbcType=VARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = GETDATE(), REG_DT = NULL, REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
AND ONEC_NO = #{onecNo, jdbcType=BIGINT} AND ATT_SEQ = #{attSeq, jdbcType=INTEGER} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wo.dao.WoOnecAttFileDDao.deleteWoOnecAttFileD */
DELETE FROM WO_ONEC_ATT_FILE_D

	AND ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND ATT_SEQ = #{attSeq, jdbcType=INTEGER}
	${whereSqllet}
