mapper.select:
/* com.innobiz.orange.web.wo.dao.WoOnecCdDDao.selectWoOnecCdD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.CLS_CD DESC, N.CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.CLS_CD,
	T.CD,
	T.CD_VA,
	T.SORT_ORDR,
	T.USE_YN,
	T.NOTE,

FROM WO_ONEC_CD_D T

	AND T.CLS_CD = #{clsCd, jdbcType=VARCHAR}
	AND T.CD = #{cd, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.CLS_CD, T.CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wo.dao.WoOnecCdDDao.countWoOnecCdD */
SELECT COUNT(*) CNT
FROM WO_ONEC_CD_D T

	AND T.CLS_CD = #{clsCd, jdbcType=VARCHAR}
	AND T.CD = #{cd, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wo.dao.WoOnecCdDDao.insertWoOnecCdD */ INSERT INTO WO_ONEC_CD_D
CLS_CD, CD, CD_VA, SORT_ORDR, USE_YN, NOTE,
#{clsCd, jdbcType=VARCHAR}, #{cd, jdbcType=VARCHAR}, #{cdVa, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM WO_ONEC_CD_D), #{useYn, jdbcType=VARCHAR}, #{note, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wo.dao.WoOnecCdDDao.updateWoOnecCdD */
UPDATE WO_ONEC_CD_D

	CLS_CD = #{clsCd, jdbcType=VARCHAR},
	CD = #{cd, jdbcType=VARCHAR},
	CD_VA = #{cdVa, jdbcType=VARCHAR},
	SORT_ORDR = NULL#{sortOrdr, jdbcType=INTEGER},
	USE_YN = #{useYn, jdbcType=VARCHAR},
	NOTE = #{note, jdbcType=VARCHAR},


	AND CLS_CD = #{clsCd, jdbcType=VARCHAR}
	AND CD = #{cd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wo.dao.WoOnecCdDDao.deleteWoOnecCdD */
DELETE FROM WO_ONEC_CD_D

	AND CLS_CD = #{clsCd, jdbcType=VARCHAR}
	AND CD = #{cd, jdbcType=VARCHAR}
	${whereSqllet}
