mapper.select:
/* com.innobiz.orange.web.wo.dao.WoOnecHisLDao.selectWoOnecHisL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.ONEC_NO DESC, N.HIS_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.ONEC_NO,
	T.HIS_NO,
	CONVERT(VARCHAR(19), T.HIS_REG_DT, 120) HIS_REG_DT,
	T.HIS_CONT,

FROM WO_ONEC_HIS_L T

	AND T.ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND T.HIS_NO = #{hisNo, jdbcType=INTEGER}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.ONEC_NO, T.HIS_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wo.dao.WoOnecHisLDao.countWoOnecHisL */
SELECT COUNT(*) CNT
FROM WO_ONEC_HIS_L T

	AND T.ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND T.HIS_NO = #{hisNo, jdbcType=INTEGER}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wo.dao.WoOnecHisLDao.insertWoOnecHisL */ INSERT INTO WO_ONEC_HIS_L
ONEC_NO, HIS_NO, HIS_REG_DT, HIS_CONT,
#{onecNo, jdbcType=BIGINT}, #{hisNo, jdbcType=INTEGER}, (SELECT ISNULL(MAX(HIS_NO),0)+1 FROM WO_ONEC_HIS_L WHERE ONEC_NO = #{onecNo, jdbcType=VARCHAR}), GETDATE(),NULL, CONVERT(DATETIME, #{hisRegDt, jdbcType=VARCHAR}, 120), #{hisCont, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wo.dao.WoOnecHisLDao.updateWoOnecHisL */
UPDATE WO_ONEC_HIS_L

	ONEC_NO = #{onecNo, jdbcType=BIGINT},
	HIS_NO = #{hisNo, jdbcType=INTEGER},
	
		HIS_REG_DT = GETDATE(),
		HIS_REG_DT = NULL,
		HIS_REG_DT = CONVERT(DATETIME, #{hisRegDt, jdbcType=VARCHAR}, 120),
	
	HIS_CONT = #{hisCont, jdbcType=VARCHAR},


	AND ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND HIS_NO = #{hisNo, jdbcType=INTEGER}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wo.dao.WoOnecHisLDao.deleteWoOnecHisL */
DELETE FROM WO_ONEC_HIS_L

	AND ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND HIS_NO = #{hisNo, jdbcType=INTEGER}
	AND HIS_NO > #{minHisNo, jdbcType=INTEGER}
	${whereSqllet}
