mapper.select:
/* com.innobiz.orange.web.wo.dao.WoOnecPichDDao.selectWoOnecPichD */

SELECT

	M.*,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = M.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.ONEC_NO DESC, N.PICH_TYP_CD DESC, N.USER_UID DESC) AS RNUM, N.* FROM (SELECT
	
	T.ONEC_NO,
	T.PICH_TYP_CD,
	T.USER_UID,
	T.SORT_ORDR,
	
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,
	

FROM WO_ONEC_PICH_D T

	AND T.ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND T.PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.ONEC_NO, T.PICH_TYP_CD, T.USER_UID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M
ORDER BY ${orderBy}M.ONEC_NO DESC, M.PICH_TYP_CD DESC, M.USER_UID DESC

/* com.innobiz.orange.web.wo.dao.WoOnecPichDDao.countWoOnecPichD */
SELECT COUNT(*) CNT
FROM WO_ONEC_PICH_D T

	AND T.ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND T.PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wo.dao.WoOnecPichDDao.insertWoOnecPichD */ INSERT INTO WO_ONEC_PICH_D
ONEC_NO, PICH_TYP_CD, USER_UID, SORT_ORDR,
#{onecNo, jdbcType=BIGINT}, #{pichTypCd, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM WO_ONEC_PICH_D),

mapper.update:
/* com.innobiz.orange.web.wo.dao.WoOnecPichDDao.updateWoOnecPichD */
UPDATE WO_ONEC_PICH_D

	ONEC_NO = #{onecNo, jdbcType=BIGINT},
	PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR},
	USER_UID = #{userUid, jdbcType=VARCHAR},
	SORT_ORDR = NULL#{sortOrdr, jdbcType=INTEGER},


	AND ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wo.dao.WoOnecPichDDao.deleteWoOnecPichD */
DELETE FROM WO_ONEC_PICH_D

	AND ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}
