mapper.select:
/* com.innobiz.orange.web.wp.dao.WpPrjBDao.selectWpPrjB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.PRJ_NO DESC) AS RNUM, N.* FROM (SELECT
T.PRJ_NO, T.VER, T.PRJ_CD, T.PRJ_NM, T.GRP_ID, CONVERT(VARCHAR(10), T.STRT_YMD, 120) STRT_YMD, CONVERT(VARCHAR(10), T.END_YMD, 120) END_YMD, CONVERT(VARCHAR(10), T.CMPL_YMD, 120) CMPL_YMD, T.PRJ_MM, T.PRJ_AMT, T.PM_ID, T.PM_MP_TYP_CD, T.SMRY, T.CUST_NM, T.CUST_ZIP_NO, T.CUST_ADR, T.PRJ_STAT_CD, T.MOD_STAT_CD, T.MOD_CONT, T.REJT_RSON, CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT, T.REG_UID, (CASE WHEN T.PM_MP_TYP_CD = 'emp' THEN (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.PM_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) WHEN T.PM_MP_TYP_CD = 'out' THEN (SELECT MP_NM FROM WP_MP_B WHERE MP_ID = T.PM_ID) ELSE NULL END) PM_NM,
FROM WP_PRJ_${tableType} T, WP_PRJ_MP_PLAN_${tableDType} A
AND T.PRJ_NO = #{prjNo, jdbcType=BIGINT} AND T.VER = #{ver, jdbcType=NUMERIC} AND T.PRJ_CD = #{prjCd, jdbcType=VARCHAR} AND T.PRJ_NM LIKE '%' + #{prjNm, jdbcType=NVARCHAR} + '%' AND T.GRP_ID = #{grpId, jdbcType=VARCHAR} AND T.END_YMD >= CONVERT(DATETIME, #{strtYmd, jdbcType=VARCHAR}, 120) AND T.STRT_YMD <= CONVERT(DATETIME, #{endYmd, jdbcType=VARCHAR}, 120) AND T.PM_ID = #{pmId, jdbcType=VARCHAR} AND T.MOD_STAT_CD = #{modStatCd, jdbcType=VARCHAR} AND T.REG_UID = #{regUid, jdbcType=VARCHAR} AND T.PRJ_NO IN ( #{item, jdbcType=VARCHAR} ) AND T.MOD_STAT_CD IN ( #{item, jdbcType=VARCHAR} )
AND T.PRJ_NO = A.PRJ_NO AND A.MP_ID = #{mpId, jdbcType=VARCHAR} AND A.PRJ_ROLE2_CD = #{prjRole2Cd, jdbcType=VARCHAR}
${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.PRJ_NO
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wp.dao.WpPrjBDao.countWpPrjB */
SELECT COUNT(*) CNT
FROM WP_PRJ_${tableType} T, WP_PRJ_MP_PLAN_${tableDType} A

	AND T.PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND T.VER = #{ver, jdbcType=NUMERIC}
	AND T.PRJ_CD = #{prjCd, jdbcType=VARCHAR}
	AND T.PRJ_NM LIKE '%' + #{prjNm, jdbcType=NVARCHAR} + '%'
	AND T.GRP_ID = #{grpId, jdbcType=VARCHAR}
	AND T.END_YMD >= CONVERT(DATETIME, #{strtYmd, jdbcType=VARCHAR}, 120)
	AND T.STRT_YMD <= CONVERT(DATETIME, #{endYmd, jdbcType=VARCHAR}, 120)
	AND T.PM_ID = #{pmId, jdbcType=VARCHAR}
	AND T.MOD_STAT_CD = #{modStatCd, jdbcType=VARCHAR}
	AND T.REG_UID = #{regUid, jdbcType=VARCHAR}
	AND T.PRJ_NO IN ( #{item, jdbcType=VARCHAR} )
	AND T.MOD_STAT_CD IN ( #{item, jdbcType=VARCHAR} )
	
		AND T.PRJ_NO = A.PRJ_NO
		AND A.MP_ID = #{mpId, jdbcType=VARCHAR}
		AND A.PRJ_ROLE2_CD = #{prjRole2Cd, jdbcType=VARCHAR}
	
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wp.dao.WpPrjBDao.insertWpPrjB */ INSERT INTO WP_PRJ_${tableType}
PRJ_NO, VER, PRJ_CD, PRJ_NM, GRP_ID, STRT_YMD, END_YMD, CMPL_YMD, PRJ_MM, PRJ_AMT, PM_ID, PM_MP_TYP_CD, SMRY, CUST_NM, CUST_ZIP_NO, CUST_ADR, PRJ_STAT_CD, MOD_STAT_CD, MOD_CONT, REJT_RSON, REG_DT, REG_UID,
#{prjNo, jdbcType=BIGINT}, #{ver, jdbcType=NUMERIC}, #{prjCd, jdbcType=VARCHAR}, #{prjNm, jdbcType=NVARCHAR}, #{grpId, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{strtYmd, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{endYmd, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{cmplYmd, jdbcType=VARCHAR}, 120), NULL#{prjMm, jdbcType=NUMERIC}, NULL#{prjAmt, jdbcType=BIGINT}, #{pmId, jdbcType=VARCHAR}, #{pmMpTypCd, jdbcType=VARCHAR}, #{smry, jdbcType=VARCHAR}, #{custNm, jdbcType=VARCHAR}, #{custZipNo, jdbcType=VARCHAR}, #{custAdr, jdbcType=VARCHAR}, #{prjStatCd, jdbcType=VARCHAR}, #{modStatCd, jdbcType=VARCHAR}, #{modCont, jdbcType=VARCHAR}, #{rejtRson, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{regUid, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wp.dao.WpPrjBDao.updateWpPrjB */ UPDATE WP_PRJ_${tableType}
PRJ_NO = #{prjNo, jdbcType=BIGINT}, PRJ_CD = #{prjCd, jdbcType=VARCHAR}, PRJ_NM = #{prjNm, jdbcType=NVARCHAR}, GRP_ID = #{grpId, jdbcType=VARCHAR},
STRT_YMD = GETDATE(), STRT_YMD = NULL, STRT_YMD = CONVERT(DATETIME, #{strtYmd, jdbcType=VARCHAR}, 120),
END_YMD = GETDATE(), END_YMD = NULL, END_YMD = CONVERT(DATETIME, #{endYmd, jdbcType=VARCHAR}, 120),
CMPL_YMD = GETDATE(), CMPL_YMD = NULL, CMPL_YMD = CONVERT(DATETIME, #{cmplYmd, jdbcType=VARCHAR}, 120),
PRJ_MM = NULL#{prjMm, jdbcType=NUMERIC}, PRJ_AMT = NULL#{prjAmt, jdbcType=BIGINT}, PM_ID = #{pmId, jdbcType=VARCHAR}, PM_MP_TYP_CD = #{pmMpTypCd, jdbcType=VARCHAR}, SMRY = #{smry, jdbcType=VARCHAR}, CUST_NM = #{custNm, jdbcType=VARCHAR}, CUST_ZIP_NO = #{custZipNo, jdbcType=VARCHAR}, CUST_ADR = #{custAdr, jdbcType=VARCHAR}, PRJ_STAT_CD = #{prjStatCd, jdbcType=VARCHAR}, MOD_STAT_CD = #{modStatCd, jdbcType=VARCHAR}, MOD_CONT = #{modCont, jdbcType=VARCHAR}, REJT_RSON = #{rejtRson, jdbcType=VARCHAR},
REG_DT = GETDATE(), REG_DT = NULL, REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
REG_UID = #{regUid, jdbcType=VARCHAR},
AND PRJ_NO = #{prjNo, jdbcType=BIGINT} AND VER = #{ver, jdbcType=NUMERIC} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wp.dao.WpPrjBDao.deleteWpPrjB */
DELETE FROM WP_PRJ_${tableType}

	AND PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND VER = #{ver, jdbcType=NUMERIC}
	${whereSqllet}
