mapper.select:
/* com.innobiz.orange.web.wp.dao.WpPrjGrpBDao.selectWpPrjGrpB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.GRP_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.GRP_ID,
	T.GRP_NM,

FROM WP_PRJ_GRP_B T

	AND T.GRP_ID = #{grpId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.GRP_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wp.dao.WpPrjGrpBDao.countWpPrjGrpB */
SELECT COUNT(*) CNT
FROM WP_PRJ_GRP_B T

	AND T.GRP_ID = #{grpId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wp.dao.WpPrjGrpBDao.insertWpPrjGrpB */ INSERT INTO WP_PRJ_GRP_B
GRP_ID, GRP_NM,
#{grpId, jdbcType=VARCHAR}, #{grpNm, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.wp.dao.WpPrjGrpBDao.updateWpPrjGrpB */
UPDATE WP_PRJ_GRP_B

	GRP_ID = #{grpId, jdbcType=VARCHAR},
	GRP_NM = #{grpNm, jdbcType=NVARCHAR},


	AND GRP_ID = #{grpId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wp.dao.WpPrjGrpBDao.deleteWpPrjGrpB */
DELETE FROM WP_PRJ_GRP_B

	AND GRP_ID = #{grpId, jdbcType=VARCHAR}
	${whereSqllet}
