mapper.select:
/* com.innobiz.orange.web.wr.dao.WrRescImgDDao.selectWrRescImgD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.RESC_MNG_ID ASC) AS RNUM, N.* FROM ( SELECT
WRI_T.RESC_MNG_ID, WRI_T.IMG_PATH, WRI_T.IMG_WDTH, WRI_T.IMG_HGHT, WRI_T.MODR_UID, CONVERT(VARCHAR(19), WRI_T.MOD_DT, 120) MOD_DT,
FROM WR_RESC_IMG_D WRI_T
ORDER BY ${orderBy} ORDER BY WRI_T.RESC_MNG_ID ASC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wr.dao.WrRescImgDDao.countWrRescImgD */
SELECT COUNT(*) CNT
FROM WR_RESC_IMG_D WRI_T

mapper.insert:
/* com.innobiz.orange.web.wr.dao.WrRescImgDDao.insertWrRescImgD */ INSERT INTO WR_RESC_IMG_D
RESC_MNG_ID , IMG_PATH, IMG_WDTH, IMG_HGHT, MODR_UID, MOD_DT,
#{rescMngId, jdbcType=VARCHAR}, #{imgPath, jdbcType=VARCHAR}, #{imgWdth, jdbcType=INTEGER}, #{imgHght, jdbcType=INTEGER}, #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wr.dao.WrRescImgDDao.updateWrRescImgD */ UPDATE WR_RESC_IMG_D
IMG_PATH = #{imgPath, jdbcType=VARCHAR}, IMG_WDTH = #{imgWdth, jdbcType=INTEGER}, IMG_HGHT = #{imgHght, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND RESC_MNG_ID = #{rescMngId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wr.dao.WrRescImgDDao.deleteWrRescImgD */
DELETE FROM WR_RESC_IMG_D

	 AND RESC_MNG_ID = #{rescMngId, jdbcType=VARCHAR}
	 ${whereSqllet}
