mapper.select:
/* com.innobiz.orange.web.wr.dao.WrRescBDao.selectWrRescB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.RESC_ID DESC) AS RNUM, N.* 
	FROM (
		SELECT
	
	WR_T.RESC_ID,	WR_T.LANG_TYP_CD, WR_T.RESC_VA ,

FROM WR_RESC_B WR_T


	ORDER BY ${orderBy}
	ORDER BY WR_T.RESC_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wr.dao.WrRescBDao.countWrRescB */
SELECT COUNT(*) CNT
FROM WR_RESC_B WR_T

mapper.insert:
/* com.innobiz.orange.web.wr.dao.WrRescBDao.insertWrRescB */ INSERT INTO WR_RESC_B
RESC_ID, LANG_TYP_CD, RESC_VA ,
#{rescId, jdbcType=VARCHAR},#{langTypCd, jdbcType=VARCHAR},#{rescVa, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wr.dao.WrRescBDao.updateWrRescB */
UPDATE WR_RESC_B

	 RESC_VA = #{rescVa, jdbcType=VARCHAR},


	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wr.dao.WrRescBDao.deleteWrRescB */
DELETE FROM WR_RESC_B

	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
