SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.USER_UID DESC, N.BX_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.USER_UID,
	T.BX_ID,
	T.SORT_ORDR,

FROM WR_RESC_PLT_SETUP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.PLT_ID = #{pltId, jdbcType=VARCHAR}
	AND T.BX_ID = #{bxId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.USER_UID, T.BX_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wr.dao.WrRescPltSetupDDao.countWrRescPltSetupD */
SELECT COUNT(*) CNT
FROM WR_RESC_PLT_SETUP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.PLT_ID = #{pltId, jdbcType=VARCHAR}
	AND T.BX_ID = #{bxId, jdbcType=VARCHAR}
	${whereSqllet}

