mapper.select:
/* com.innobiz.orange.web.ap.dao.ApApvLnGrpBDao.countApApvLnGrpB */
SELECT COUNT(*) CNT
FROM AP_APV_LN_GRP_B T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.APV_LN_GRP_ID = #{apvLnGrpId, jdbcType=VARCHAR}
	AND T.APV_LN_GRP_TYP_CD = #{apvLnGrpTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
