USER_UID, APV_LN_GRP_ID, APV_LN_GRP_NM, RESC_ID, SORT_ORDR, MODR_UID, MOD_DT, APV_LN_GRP_TYP_CD,
#{userUid, jdbcType=VARCHAR}, #{apvLnGrpId, jdbcType=VARCHAR}, #{apvLnGrpNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM AP_APV_LN_GRP_B P WHERE P.USER_UID = #{userUid, jdbcType=VARCHAR} AND P.APV_LN_GRP_TYP_CD = #{apvLnGrpTypCd, jdbcType=VARCHAR}), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{apvLnGrpTypCd, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApApvLnGrpBDao.updateApApvLnGrpB */ UPDATE AP_APV_LN_GRP_B
USER_UID = #{userUid, jdbcType=VARCHAR}, APV_LN_GRP_ID = #{apvLnGrpId, jdbcType=VARCHAR}, APV_LN_GRP_NM = #{apvLnGrpNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
