mapper.select:
/* com.innobiz.orange.web.ap.dao.ApFormBxDeptRDao.selectApFormBxDeptR */
SELECT

	T.FORM_BX_ID,
	T.FORM_BX_DEPT_ID,
	T.SORT_ORDR,

FROM AP_FORM_BX_DEPT_R T

	AND T.FORM_BX_ID = #{formBxId, jdbcType=VARCHAR}
	AND T.FORM_BX_DEPT_ID = #{formBxDeptId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.FORM_BX_ID, T.FORM_BX_DEPT_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApFormBxDeptRDao.countApFormBxDeptR */
SELECT COUNT(*) CNT
FROM AP_FORM_BX_DEPT_R T

	AND T.FORM_BX_ID = #{formBxId, jdbcType=VARCHAR}
	AND T.FORM_BX_DEPT_ID = #{formBxDeptId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApFormBxDeptRDao.insertApFormBxDeptR */ INSERT INTO AP_FORM_BX_DEPT_R
FORM_BX_ID, FORM_BX_DEPT_ID, SORT_ORDR,
#{formBxId, jdbcType=VARCHAR}, #{formBxDeptId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM AP_FORM_BX_DEPT_R P WHERE P.FORM_BX_ID = #{formBxId, jdbcType=VARCHAR}),

