FROM AP_${storage}_APV_LN_${tableType} T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.APV_LN_PNO = #{apvLnPno, jdbcType=INTEGER}
	AND T.APV_LN_NO = #{apvLnNo, jdbcType=INTEGER}
	AND T.APV_LN_HST_NO = #{apvLnHstNo, jdbcType=INTEGER}
	AND T.DBL_APV_TYP_CD = #{dblApvTypCd, jdbcType=VARCHAR}
	AND T.APVR_ROLE_CD = #{apvrRoleCd, jdbcType=VARCHAR}
	AND T.APVR_DEPT_YN = #{apvrDeptYn, jdbcType=VARCHAR}
	AND T.APVR_UID = #{apvrUid, jdbcType=VARCHAR}
	AND T.APV_DEPT_ID = #{apvDeptId, jdbcType=VARCHAR}
	AND T.APVR_ROLE_CD IN ( #{item, jdbcType=VARCHAR} )
	AND T.APV_STAT_CD IN ( #{item, jdbcType=VARCHAR} )
	 AND APV_LN_NO >= #{nextApvLnNo, jdbcType=INTEGER}
	${whereSqllet}

