SET APV_LN_NO = APV_LN_NO - 1
WHERE APV_NO = #{apvNo, jdbcType=BIGINT}
	AND APV_LN_PNO = 0
	AND APV_LN_NO > #{apvLnNo, jdbcType=INTEGER}
