mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdBodyLDao.countApOngdBodyL */
SELECT COUNT(*) CNT
FROM AP_${storage}_BODY_L T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.BODY_HST_NO = #{bodyHstNo, jdbcType=INTEGER}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdBodyLDao.insertApOngdBodyL */ INSERT INTO AP_${storage}_BODY_L
APV_NO, BODY_HST_NO, MODR_UID, MOD_DT, BODY_HTML,
#{apvNo, jdbcType=BIGINT}, #{bodyHstNo, jdbcType=INTEGER}, #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{bodyHtml, jdbcType=CLOB},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdBodyLDao.updateApOngdBodyL */
UPDATE AP_${storage}_BODY_L

	APV_NO = #{apvNo, jdbcType=BIGINT},
	BODY_HST_NO = #{bodyHstNo, jdbcType=INTEGER},
	MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		MOD_DT = NOW(),
		MOD_DT = NULL,
		MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	
	BODY_HTML = #{bodyHtml, jdbcType=CLOB},


	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND BODY_HST_NO = #{bodyHstNo, jdbcType=INTEGER}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdBodyLDao.deleteApOngdBodyL */
DELETE FROM AP_${storage}_BODY_L

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND BODY_HST_NO = #{bodyHstNo, jdbcType=INTEGER}
	${whereSqllet}
