mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdErpFormDDao.selectApOngdErpFormD */
SELECT

	T.APV_NO,
	T.ERP_VA_TYP_CD,
	T.ERP_VA,

FROM AP_${storage}_ERP_FORM_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.ERP_VA_TYP_CD = #{erpVaTypCd, jdbcType=VARCHAR}
	AND T.APV_NO IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.APV_NO, T.ERP_VA_TYP_CD

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdErpFormDDao.countApOngdErpFormD */
SELECT COUNT(*) CNT
FROM AP_${storage}_ERP_FORM_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.ERP_VA_TYP_CD = #{erpVaTypCd, jdbcType=VARCHAR}
	AND T.APV_NO IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdErpFormDDao.insertApOngdErpFormD */ INSERT INTO AP_${storage}_ERP_FORM_D
APV_NO, ERP_VA_TYP_CD, ERP_VA,
#{apvNo, jdbcType=BIGINT}, #{erpVaTypCd, jdbcType=VARCHAR}, #{erpVa, jdbcType=CLOB},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdErpFormDDao.updateApOngdErpFormD */
UPDATE AP_${storage}_ERP_FORM_D

	APV_NO = #{apvNo, jdbcType=BIGINT},
	ERP_VA_TYP_CD = #{erpVaTypCd, jdbcType=VARCHAR},
	ERP_VA = #{erpVa, jdbcType=CLOB},


	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND ERP_VA_TYP_CD = #{erpVaTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdErpFormDDao.deleteApOngdErpFormD */
DELETE FROM AP_${storage}_ERP_FORM_D

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND ERP_VA_TYP_CD = #{erpVaTypCd, jdbcType=VARCHAR}
	${whereSqllet}
