mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.selectApOngdPubBxD */
SELECT

	T.PUB_BX_DEPT_ID,
	T.APV_NO,
	DATE_FORMAT(T.PUB_BX_DT, '%Y-%m-%d %H:%i:%s') PUB_BX_DT,
	DATE_FORMAT(T.PUB_BX_END_YMD, '%Y-%m-%d') PUB_BX_END_YMD,
	T.REGR_UID,
	T.REGR_NM,
	T.REG_DEPT_ID,
	T.REG_DEPT_NM,

FROM AP_${storage}_PUB_BX_D T

	AND T.PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.PUB_BX_DEPT_ID, T.APV_NO

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.countApOngdPubBxD */
SELECT COUNT(*) CNT
FROM AP_${storage}_PUB_BX_D T

	AND T.PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.insertApOngdPubBxD */ INSERT INTO AP_${storage}_PUB_BX_D
PUB_BX_DEPT_ID, APV_NO, PUB_BX_DT, PUB_BX_END_YMD, REGR_UID, REGR_NM, REG_DEPT_ID, REG_DEPT_NM,
